import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { MyService } from 'src/app/services/my.service';

@Component({
  selector: 'app-mybookings',
  templateUrl: './mybookings.component.html',
  styleUrls: ['./mybookings.component.css']
})
export class MybookingsComponent {
  table;
  bookingForm: any;
constructor(private getm:MyService,private fb: FormBuilder){

}
ngOnInit(){
  this.getm.getbookings().subscribe((res)=>{
    this.table=res;
    this.bookingForm = this.fb.group({
      bookingId: ['', [Validators.required]],
      fromDestination: ['', [Validators.required]],
      toDestination: ['', [Validators.required]]
    });
  })
}
onSubmit() {
  if (this.bookingForm.valid) {
    const updatedData = this.bookingForm.value; // Get form values

    // Call the service method to update the booking
    this.getm.updatebooking(updatedData.bookingId, updatedData).subscribe(
      (response) => {
        console.log('Booking updated successfully', response);
      },
      (error) => {
        console.error('Error updating booking', error);
      }
    );
  }


}
<table class="table table-striped table-hover table-bordered">
  <thead class="thead-dark">
    <tr>
      <th>Booking Id</th>
      <th>From Destination</th>
      <th>To Destination</th>
      <th>Price (USD)</th>
      <th>Date</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let travel of table">
      <td>{{ travel.id }}</td>
      <td>{{ travel.fromDestination }}</td>
      <td>{{ travel.toDestination }}</td>
      <td>${{ travel.price }}</td>
      <td>{{ travel.date }}</td>
    </tr>
  </tbody>
</table>
<div class="container">
  <h2>Update Booking Details</h2>

  <form [formGroup]="bookingForm" (ngSubmit)="onSubmit()">
    <!-- Booking ID -->
    <div class="form-group">
      <label for="bookingId">Booking ID</label>
      <input type="text" id="bookingId" formControlName="bookingId" class="form-control" placeholder="Enter Booking ID" />
    </div>

    <!-- From Destination -->
    <div class="form-group">
      <label for="fromDestination">From Destination</label>
      <input type="text" id="fromDestination" formControlName="fromDestination" class="form-control" placeholder="Enter From Destination" />
    </div>

    <!-- To Destination -->
    <div class="form-group">
      <label for="toDestination">To Destination</label>
      <input type="text" id="toDestination" formControlName="toDestination" class="form-control" placeholder="Enter To Destination" />
    </div>

    <!-- Submit Button -->
    <button type="submit" class="btn btn-primary" [disabled]="!bookingForm.valid">Update Booking</button>
  </form>
</div>
