import { Component } from '@angular/core';

@Component({
  selector: 'app-navbarc',
  templateUrl: './navbarc.component.html',
  styleUrls: ['./navbarc.component.css']
})
export class NavbarcComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Navbar Example</title>
    <style>
        /* Basic styling for the body */
        body {
            margin: 0;
            padding: 0;
            font-family: Arial, sans-serif;
        }

        /* Navbar styling */
        nav {
            display: flex;
            justify-content: space-between;
            align-items: center;
            background-color: #333;
            padding: 10px 20px;
        }

        /* Logo image on the left */
        .navbar-logo img {
            height: 50px;
            width: 50px;
        }

        /* Links to the right */
        .navbar-links {
            display: flex;
            flex-grow: 1;
            justify-content: center;
            gap: 30px; /* Add space between the links */
        }

        /* Navigation links styling */
        .navbar-links a {
            color: white;
            text-decoration: none;
            font-size: 18px;
            padding: 8px 16px;
        }

        /* Right-aligned section for the "Reviews" */
        .navbar-right {
            margin-left: auto;
        }

        /* Hover effect for links */
        .navbar-links a:hover, .navbar-right a:hover {
            background-color: #555;
            border-radius: 5px;
        }

    </style>
</head>
<body>

    <!-- Navbar section -->
    <nav>
        <!-- Left-aligned logo -->
        <div class="navbar-logo">
            <img src="logo.png" alt="Logo">
        </div>

        <!-- Center-aligned navigation links -->
        <div class="navbar-links">
            <a href="#">Home</a>
            <a href="#">About Us</a>
            <a href="#">Register</a>
        </div>

        <!-- Right-aligned reviews link -->
        <div class="navbar-right">
            <a href="#">Reviews</a>
        </div>
    </nav>

</body>
</html>
