import { Component } from '@angular/core';

@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Background Image with Text and Button</title>
  <style>
    /* Container for the background image */
    .image-container {
      position: relative;  /* Allows absolute positioning inside */
      text-align: center;  /* Centers the text and button horizontally */
      color: white;  /* Text color */
      font-family: Arial, sans-serif;
      height: 400px;  /* Set a height for the container */
      background-size: cover;
    }

    /* Background image styling */
    .image-container img {
      width: 100%;  /* Make the image full width */
      height: 100%; /* Make the image full height */
      object-fit: cover;  /* Ensure the image covers the container area */
    }

    /* Text over the image */
    .image-text {
      position: absolute;
      top: 50%;  /* Vertical center */
      left: 200px; /* Horizontal center */
      transform: translate(-50%, -50%); /* Center align */
      font-size: 2rem;
      font-weight: bold;
    }

    /* Button styling */
    .image-button {
      position: absolute;
      top: 65%;  
      left: 250px; /* Horizontal center */
      transform: translate(-50%, -50%);
      background-color: #ff5722; /* Button background color */
      color: white;  /* Button text color */
      border: none;  /* Remove border */
      padding: 10px 20px;  /* Padding inside button */
      font-size: 1.2rem;
      cursor: pointer;
      border-radius: 5px; /* Rounded corners */
    }


    .image-button:hover {
      background-color: #e64a19; 
    }
  </style>
</head>
<body>

  <div class="image-container">

    <img src="../../../assets/images/bgimage.jpg" height="2000" width="2000" alt="Background Image">


    <div class="image-text">Explore the World</div>


    <button class="image-button">Book Now</button>
  </div>

</body>
</html>
